        </tbody>
      </table>
      
      {selectedVisitor && (
        <div className="update-form">
          <h2>Edit Visitor</h2>
          <form >
          <input type="text" placeholder="name"
				 />
				
				<input type="email" placeholder="email"
				 />
            <button type="submit">Save</button>
            <button type="button" onClick={handleUpdateFormClose}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

