import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Data() {
  const [data, setData] = useState([]);
  const [selectedVisitor, setSelectedVisitor] = useState(null);

  const fetchData = () => {
    axios
      .get('http://localhost:5000/api/data')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const handleUpdateClick = (visitor) => {
    setSelectedVisitor(visitor);
  };

  useEffect(() => {
    fetchData();

    const intervalId = setInterval(() => {
      fetchData();
    }, 1000);

    return () => clearInterval(intervalId);
  }, []);

  const handleUpdateFormClose = () => {
    setSelectedVisitor(null);
  };

  const handleUpdateFormSubmit = (updatedData) => {
    // Handle the update form submission here using axios or any other method
    // Once the update is complete, close the form
    handleUpdateFormClose();
  };

  return (
    <div className="App">
      <h1>Visitors Names</h1>
      <table>
        <thead>
          <tr>
            <th>Visitor Name</th>
            <th>Visitor Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>
                <button onClick={() => handleUpdateClick(item)}>Update</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      
      {selectedVisitor && (
        <div className="update-form">
          <h2>Edit Visitor</h2>
          <form >
          <input type="text" placeholder="name"
				 />
				
				<input type="email" placeholder="email"
				 />
            <button type="submit">Save</button>
            <button type="button" onClick={handleUpdateFormClose}>Cancel</button>
          </form>
        </div>
      )}
    </div>
  );
}

export default Data;
