import { useState } from 'react'


function Update(props) {
   
    const { id,initialName,initialEmail } = props;
	const [name, setName] = useState(initialName || "");
	const [email, setEmail] = useState(initialEmail || "");

    console.log('initialName:', initialName);
    console.log('initialEmail:', initialEmail);

	const handleOnSubmit = async (e) => {
		e.preventDefault();
		let result = await fetch(
		'http://localhost:5000/update', {
			method: "put",
			body: JSON.stringify({id, name, email }),
			headers: {
				'Content-Type': 'application/json'
			}
		})
		result = await result.json();
		console.warn(result);
		if (result) {
			alert("Data updated succesfully");
			setEmail("");
			setName("");
            window.location.reload();

		}
	}
	return (
		<div>

            
			<h1>Update visitor id:  </h1>
            <p>visitor id: <small><b>{id}</b></small> </p>

			<form action="">
           
				<input type="text" placeholder="name"
				value={name} onChange={(e) => setName(e.target.value)} />
				
				<input type="email" placeholder="email"
				value={email} onChange={(e) => setEmail(e.target.value)} />
           
				<button type="submit"
				onClick={handleOnSubmit}>save</button>

			</form>

		</div>
	);
}

export default Update;
