import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Update from './update'; 
import Delete from './delete'; 




function Data() {
  
  const [data, setData] = useState([]);

  const [isUpdateVisible, setUpdateVisible] = useState(false);

  const [isDeleteVisible, setDeleteVisible] = useState(false);

  const [selectedId, setSelectedId] = useState(null);
  const [selectedName, setSelectedName] = useState(null);
  const [selectedEmail, setSelectedEmail] = useState(null);

  const handleUpdateButtonClick = (id,name,email) => {
    setSelectedId(id);
    setSelectedName(name);
    setSelectedEmail(email);
    setUpdateVisible(true);
  };

  const handleDeleteButtonClick = (id,name,email) => {
    setSelectedId(id);
    setSelectedName(name);
    setSelectedEmail(email);
    setUpdateVisible(true);
  };

  const fetchData = () => {
    axios
      .get('http://localhost:5000/api/data')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    fetchData(); 

    const intervalId = setInterval(() => {
      fetchData(); 
    }, 1000);

   
    return () => clearInterval(intervalId);
  }, []); 

  
  return (
    <div className="App">
      <h1>Visitors Names</h1>
      <table>
        <thead>
          <tr>
            <th>Visitor Name</th>
            <th>Visitor Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
               
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>
              <button onClick={() => handleUpdateButtonClick(item._id,item.name,item.email)}>Update</button>
      
                <button>Delete</button>
                
              </td>

            </tr>
          ))}
         
        </tbody>
        
      </table>
      
      {isUpdateVisible && <Update id={selectedId} initialName={selectedName} initialEmail={selectedEmail} />}
    </div>



  );


 
}

export default Data;
