import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ShowHideComponent() {
  const [isVisible, setIsVisible] = useState(false);

  const toggleVisibility = () => {
    setIsVisible(!isVisible);
  };

  return (
    <div>
      {isVisible && <div>Content to show when the button is pressed</div>}
    </div>
  );
}


function Data() {
  
  const [data, setData] = useState([]);

  const fetchData = () => {
    axios
      .get('http://localhost:5000/api/data')
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  useEffect(() => {
    fetchData(); 

    const intervalId = setInterval(() => {
      fetchData(); 
    }, 1000);

   
    return () => clearInterval(intervalId);
  }, []); 

  
  return (
    <div className="App">
      <h1>Visitors Names</h1>
      <table>
        <thead>
          <tr>
            <th>Visitor Name</th>
            <th>Visitor Email</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item._id}>
               
              <td>{item.name}</td>
              <td>{item.email}</td>
              <td>
                <button onClick={toggleVisibility}>Update</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Data;
